mapper.select:
SELECT wo.WORK_ORDER_ID, wo.`STATUS` WO_STATUS, mat.DESCRIPTION WORKSHOP_NAME, plt.DESCRIPTION PROD_LINE_NAME, wa_so.ATTR_VALUE SO_NUM, wo.WORK_ORDER_NUM, mm.MATERIAL_CODE, mm.MATERIAL_ID, mmt.MATERIAL_NAME, wo_cr.ATTR_VALUE CREATION_DATE, wo_re.ATTR_VALUE RELEASE_DATE, wo_cu.ATTR_VALUE CUSTOMER_CODE, mc.CUSTOMER_NAME, wo.QTY WO_QTY, ( SELECT IFNULL(sum( EO.QTY ), 0) FROM mt_eo eo WHERE eo.WORK_ORDER_ID = wo.WORK_ORDER_ID AND eo.`STATUS` IN ( 'WORKING', 'NEW', 'RELEASED' ) AND NOT EXISTS ( SELECT 1 FROM hme_eo_job_sn sn WHERE sn.eo_id = eo.EO_ID )) WAIT_QTY, ( SELECT IFNULL(sum( eo.QTY ), 0) FROM mt_eo eo, mt_eo_router_actual ra, mt_eo_step_actual sa, mt_eo_step_wip sw WHERE sw.EO_STEP_ACTUAL_ID = sa.EO_STEP_ACTUAL_ID AND sa.EO_ROUTER_ACTUAL_ID = ra.EO_ROUTER_ACTUAL_ID AND ra.EO_ID = eo.EO_ID AND eo.WORK_ORDER_ID = wo.work_order_id AND eo.`STATUS` = 'WORKING' AND sw.WORKCELL_ID <> '' AND sw.WORKCELL_ID IS NOT NULL ) WIP_QTY, wa.COMPLETED_QTY, ( SELECT IFNULL(sum( eo.QTY ), 0) FROM mt_eo eo, mt_material_lot mml, mt_material_lot_attr la, mt_mod_locator ml, mt_mod_locator ml_p WHERE eo.IDENTIFICATION = mml.MATERIAL_LOT_CODE AND eo.WORK_ORDER_ID = wo.work_order_id AND mml.MATERIAL_LOT_ID = la.MATERIAL_LOT_ID AND la.ATTR_NAME = 'MF_FLAG' AND la.ATTR_VALUE = '' AND mml.ENABLE_FLAG = 'Y' AND mml.LOCATOR_ID = ml.LOCATOR_ID AND ml_p.LOCATOR_ID = ml.PARENT_LOCATOR_ID AND ml_p.LOCATOR_TYPE = '14' ) NOT_STOCK, ( SELECT IFNULL(sum( eo.QTY ), 0) FROM mt_eo eo, mt_material_lot mml, mt_material_lot_attr la, mt_mod_locator ml, mt_mod_locator ml_p WHERE eo.IDENTIFICATION = mml.MATERIAL_LOT_CODE AND eo.WORK_ORDER_ID = wo.work_order_id AND mml.MATERIAL_LOT_ID = la.MATERIAL_LOT_ID AND la.ATTR_NAME = 'MF_FLAG' AND la.ATTR_VALUE = '' AND mml.ENABLE_FLAG = 'Y' AND mml.LOCATOR_ID = ml.LOCATOR_ID AND ml_p.LOCATOR_ID = ml.PARENT_LOCATOR_ID AND ml_p.LOCATOR_TYPE <> '14' ) IN_STOCK, wa_re.ATTR_VALUE REMARK FROM mt_work_order wo JOIN mt_mod_production_line pl ON pl.PROD_LINE_ID = wo.PRODUCTION_LINE_ID JOIN mt_mod_production_line_tl plt ON plt.PROD_LINE_ID = pl.PROD_LINE_ID AND plt.LANG = #{lang} JOIN mt_mod_organization_rel mor ON mor.TOP_SITE_ID = #{dto.siteId} AND mor.PARENT_ORGANIZATION_TYPE = 'AREA' AND mor.ORGANIZATION_TYPE = 'PROD_LINE' AND mor.ORGANIZATION_id = pl.PROD_LINE_ID JOIN mt_mod_area ma ON ma.AREA_ID = mor.PARENT_ORGANIZATION_ID JOIN mt_mod_area_tl mat ON mat.AREA_ID = ma.AREA_ID AND mat.LANG = #{lang} JOIN mt_work_order_actual wa ON wa.WORK_ORDER_ID = wo.WORK_ORDER_ID LEFT JOIN mt_work_order_attr wa_so ON wa_so.WORK_ORDER_ID = wo.WORK_ORDER_ID AND wa_so.ATTR_NAME = 'attribute1' LEFT JOIN mt_work_order_attr wa_re ON wa_re.WORK_ORDER_ID = wo.WORK_ORDER_ID AND wa_re.ATTR_NAME = 'attribute8' LEFT JOIN mt_work_order_attr wo_cr ON wo_cr.WORK_ORDER_ID = wo.WORK_ORDER_ID AND wo_cr.ATTR_NAME = 'attribute12' LEFT JOIN mt_work_order_attr wo_re ON wo_re.WORK_ORDER_ID = wo.WORK_ORDER_ID AND wo_re.ATTR_NAME = 'attribute13' LEFT JOIN mt_work_order_attr wo_cu ON wo_cu.WORK_ORDER_ID = wo.WORK_ORDER_ID AND wo_cu.ATTR_NAME = 'attribute14' LEFT JOIN mt_customer mc ON mc.CUSTOMER_CODE = wo_cu.ATTR_VALUE JOIN mt_material mm ON mm.MATERIAL_ID = wo.MATERIAL_ID JOIN mt_material_tl mmt ON mmt.MATERIAL_ID = mm.MATERIAL_ID AND mmt.LANG = #{lang} WHERE wo.WORK_ORDER_TYPE = 'RK02'
AND wo.`STATUS` IN
#{item}
AND ma.AREA_ID = #{dto.workshopId}
AND pl.PROD_LINE_ID = #{dto.prodLineId}
AND wo.WORK_ORDER_NUM LIKE CONCAT('%', #{dto.workOrderNum}, '%')
AND wa_so.ATTR_VALUE LIKE CONCAT('%', #{dto.soNum}, '%')
AND mm.MATERIAL_ID = #{dto.materialId}
AND mm.MATERIAL_ID = #{dto.materialId}
AND wo_cu.ATTR_VALUE LIKE CONCAT('%',#{dto.customerCode}, '%')
AND wo_cr.ATTR_VALUE >= #{dto.creationStartDate}
AND wo_cr.ATTR_VALUE <= #{dto.creationEndDate}
AND wo_re.ATTR_VALUE >= #{dto.releaseStartDate}
AND wo_re.ATTR_VALUE <= #{dto.releaseEndDate}
ORDER BY wo_cr.ATTR_VALUE DESC
