mapper.select:
SELECT t.SHIFT_DATE, t.SHIFT_CODE, t.WORKCELL_ID, t.description, ROUND( t.plannedProduction, 0 ) plannedProduction, 0 actualProduction, round( t.plannedProduction, 0 ) plannedDelivery, 0 actualAelivery, ROUND( ifnull( t.quantityUnderProduction, 0 ), 0 ) quantityUnderProduction, round( ifnull( t.inProcessStandard, 0 ), 0 ) inProcessStandards, 0 actualProductionRatio, 0 actualAeliveryRatio, 0 percentageInProduction FROM ( SELECT mcs.SHIFT_DATE, mcs.SHIFT_CODE, mmw.WORKCELL_ID, mmw.WORKCELL_NAME description, mmw.WORKCELL_NAME, mcs.CALENDAR_SHIFT_ID, ( SELECT ifnull( SUM( DR.DISPATCH_QTY ), 0 ) FROM hme_wo_dispatch_recode dr WHERE dr.TENANT_ID = #{tenantId} AND dr.WORKCELL_ID = mmw.WORKCELL_ID AND dr.CALENDAR_SHIFT_ID = mcs.CALENDAR_SHIFT_ID ) plannedProduction, ( SELECT sum( wip.WORKING_QTY + wip.QUEUE_QTY + wip.COMPLETED_QTY ) sum_qty FROM mt_eo_step_wip wip, mt_mod_organization_rel mor1, mt_mod_organization_rel mor2 WHERE mor1.ORGANIZATION_ID = wip.WORKCELL_ID AND mor1.ORGANIZATION_TYPE = 'WORKCELL' AND mor1.PARENT_ORGANIZATION_TYPE = 'WORKCELL' AND mor1.PARENT_ORGANIZATION_ID = mor2.ORGANIZATION_ID AND mor2.ORGANIZATION_TYPE = 'WORKCELL' AND mor2.PARENT_ORGANIZATION_ID = mmw.WORKCELL_ID AND mor2.PARENT_ORGANIZATION_TYPE = 'WORKCELL' AND mor2.TOP_SITE_ID = mor1.TOP_SITE_ID AND mor1.TOP_SITE_ID = #{hmePlanRateReportRequestDTO.siteId} GROUP BY mor2.PARENT_ORGANIZATION_ID ) quantityUnderProduction, ( SELECT ifnull( wa.ATTR_VALUE, 0 ) FROM mt_mod_workcell_attr wa WHERE wa.WORKCELL_ID = mmw.WORKCELL_ID AND wa.ATTR_NAME = 'PROCESS_STANDARD' ) inProcessStandard FROM mt_mod_workcell mmw JOIN mt_calendar_org_rel mcor ON mcor.ORGANIZATION_ID = mmw.WORKCELL_ID AND mcor.ORGANIZATION_TYPE = 'WORKCELL' JOIN mt_calendar_shift mcs ON mcor.CALENDAR_ID = mcs.CALENDAR_ID, mt_mod_organization_rel mmor WHERE mmor.TOP_SITE_ID = #{hmePlanRateReportRequestDTO.siteId} AND mmor.PARENT_ORGANIZATION_TYPE = 'PROD_LINE' AND mmor.PARENT_ORGANIZATION_ID = #{hmePlanRateReportRequestDTO.prodLineId} AND mmor.ORGANIZATION_TYPE = 'WORKCELL' AND mmor.ORGANIZATION_ID = mmw.WORKCELL_ID AND mcor.ENABLE_FLAG = 'Y' AND mcs.ENABLE_FLAG = 'Y' AND mcs.SHIFT_DATE >= str_to_date(#{hmePlanRateReportRequestDTO.dateTimeFrom}, '%Y-%m-%d') AND mcs.SHIFT_DATE <= str_to_date(#{hmePlanRateReportRequestDTO.dateTimeTo}, '%Y-%m-%d')
and mmw.WORKCELL_ID = #{hmePlanRateReportRequestDTO.lineId}
ORDER BY mcs.SHIFT_DATE, mmor.SEQUENCE, mcs.SEQUENCE ) t

select wks.shift_date, wks.shift_code, wks.workcell_id, count(1) actualProduction from mt_mod_organization_rel mor1, mt_mod_organization_rel mor2 , hme_eo_job_sn sn, mt_wkc_shift wks, (select mor3.parent_organization_id workcell_id, min(mor3.sequence) sequence from mt_mod_organization_rel mor3 where mor3.top_site_id = #{dto.siteId} and mor3.parent_organization_type = 'WORKCELL' and mor3.parent_organization_id in
#{item}
and mor3.organization_type = 'WORKCELL' group by mor3.parent_organization_id) rel_seq where mor1.top_site_id = #{dto.siteId} and mor1.parent_organization_type = 'WORKCELL' and mor1.parent_organization_id = mor2.organization_id and mor1.organization_type = 'WORKCELL' and mor2.top_site_id = #{dto.siteId} and mor2.parent_organization_type = 'WORKCELL' and mor2.parent_organization_id = wks.workcell_id and mor2.organization_type = 'WORKCELL' and mor2.sequence = rel_seq.sequence and rel_seq.workcell_id = wks.workcell_id and sn.workcell_id = mor1.organization_id and sn.shift_id = wks.wkc_shift_id and sn.rework_flag = 'N' and sn.eo_id is not null and wks.workcell_id in
#{item}
and wks.shift_date >= str_to_date(#{dto.dateTimeFrom}, '%Y-%m-%d') and wks.shift_date <= str_to_date(#{dto.dateTimeTo}, '%Y-%m-%d') and wks.shift_code in
#{item}
and wks.tenant_id = #{tenantId} group by wks.shift_date, wks.shift_code, wks.workcell_id

SELECT mws.shift_date, mws.shift_code, mws.workcell_id, ROUND(sum(eo.qty), 0) actualAelivery FROM mt_eo eo, hme_eo_job_sn sn, mt_mod_organization_rel mmor1, mt_mod_organization_rel mmor2, mt_wkc_shift mws, mt_mod_workcell prs, mt_mod_workcell_attr mmwa WHERE 1 = 1 AND eo.eo_id = sn.eo_id AND sn.eo_id IS NOT NULL AND sn.site_out_date IS NOT NULL AND sn.rework_flag = 'N' AND sn.workcell_id = mmor2.organization_id AND mmor2.top_site_id = #{dto.siteId} AND mmor2.parent_organization_type = 'WORKCELL' AND mmor2.parent_organization_id = prs.workcell_id AND mmor2.organization_type = 'WORKCELL' AND mmor1.top_site_id = mmor2.top_site_id AND mmor1.parent_organization_type = 'WORKCELL' AND mmor1.parent_organization_id = mws.workcell_id AND mmor1.organization_type = 'WORKCELL' AND mmor1.organization_id = mmor2.parent_organization_id AND prs.workcell_id = mmwa.workcell_id AND mmwa.attr_name = 'OUTPUT_FLAG' AND mmwa.attr_value = 'Y' AND mmwa.tenant_id = prs.tenant_id AND prs.tenant_id = sn.tenant_id AND sn.tenant_id = #{tenantId} AND sn.shift_id = mws.wkc_shift_id AND mws.workcell_id in
#{item}
AND mws.shift_date >= str_to_date(#{dto.dateTimeFrom}, '%Y-%m-%d') AND mws.shift_date <= str_to_date(#{dto.dateTimeTo}, '%Y-%m-%d') AND mws.shift_code in
#{item}
group by mws.shift_date, mws.shift_code, mws.workcell_id
