SELECT mms.SITE_CODE, woa.ATTR_VALUE, wo.WORK_ORDER_NUM, wo.PRODUCTION_VERSION, mm.MATERIAL_CODE, mm.MATERIAL_NAME, mgtl.description workOrderType, mgsl.description workOrderStatus, mmpl.PROD_LINE_NAME, wo.QTY, tt.ACTUAL_QTY, tt.MATERIAL_LOT_CODE, tt.CREATION_DATE, tt.CONTAINER_CODE, tt.LOCATOR_CODE, tt.real_name, tt.INSTRUCTION_DOC_NUM, tt.INSTRUCTION_DOC_STATUS FROM mt_work_order wo LEFT JOIN mt_work_order_attr woa ON woa.WORK_ORDER_ID = wo.WORK_ORDER_ID AND woa.ATTR_NAME = 'attribute4' LEFT JOIN mt_gen_type mgt ON mgt.TYPE_CODE = wo.WORK_ORDER_TYPE AND mgt.TYPE_GROUP = 'WO_TYPE' AND mgt.MODULE = 'ORDER' LEFT JOIN mt_gen_type_tl mgtl ON mgtl.GEN_TYPE_ID = mgt.gen_type_id AND mgtl.lang = #{lang} LEFT JOIN mt_gen_status mgs ON mgs.status_code = wo.STATUS AND mgs.STATUS_GROUP = 'WO_STATUS' AND mgs.module = 'ORDER' LEFT JOIN mt_gen_status_tl mgsl ON mgsl.GEN_STATUS_ID = mgs.GEN_STATUS_ID AND mgsl.lang = #{lang} LEFT JOIN ( SELECT eo.work_order_id, mml.MATERIAL_LOT_CODE, miad.CREATION_DATE, mc.CONTAINER_CODE, ml.LOCATOR_ID, ml.LOCATOR_CODE, iu.id, iu.real_name, mid.INSTRUCTION_DOC_NUM, mid.INSTRUCTION_DOC_STATUS, miad.ACTUAL_QTY, miad.ACTUAL_DETAIL_ID FROM mt_instruction_doc mid, mt_instruction mi LEFT JOIN mt_mod_locator ml ON ml.locator_id = mi.to_locator_id, mt_instruction_actual mia, mt_instruction_actual_detail miad LEFT JOIN mt_container mc ON mc.container_id = miad.container_id, iam_user iu, mt_material_lot mml, mt_Eo eo WHERE mid.INSTRUCTION_DOC_TYPE = 'PRODUCT_RECEIPT' AND mid.INSTRUCTION_DOC_ID = mi.SOURCE_DOC_ID AND mi.INSTRUCTION_ID = mia.INSTRUCTION_ID AND mia.INSTRUCTION_ID = mi.INSTRUCTION_ID AND mia.ACTUAL_ID = miad.ACTUAL_ID AND mml.MATERIAL_LOT_ID = miad.MATERIAL_LOT_ID AND miad.CREATED_BY = iu.id AND eo.IDENTIFICATION = mml.MATERIAL_LOT_CODE ) tt ON tt.work_order_id = wo.WORK_ORDER_ID, mt_mod_site mms, mt_material mm, mt_mod_production_line mmpl WHERE wo.TENANT_ID = #{tenantId} AND wo.SITE_ID = mms.SITE_ID AND mm.material_Id = wo.MATERIAL_ID AND wo.PRODUCTION_LINE_ID = mmpl.prod_line_id
AND mms.SITE_ID = #{dto.siteId}
AND woa.ATTR_VALUE = #{dto.areaName}
AND mmpl.PROD_LINE_ID= #{dto.prodLineId}
and wo.WORK_ORDER_NUM in
#{item}
AND wo.PRODUCTION_VERSION LIKE CONCAT('%',#{dto.productionVersion},'%')
and mm.MATERIAL_CODE in
#{item}
AND mgt.TYPE_CODE = #{dto.workOrderType}
AND mgs.STATUS_CODE = #{dto.workOrderStatus}
and tt.MATERIAL_LOT_CODE in
#{item}
AND tt.CREATION_DATE >= #{dto.creationDateFrom}
AND tt.CREATION_DATE <= #{dto.creationDateFromTo}
and tt.CONTAINER_CODE in
#{item}
AND tt.LOCATOR_ID = #{dto.locatorId}
AND tt.id = #{dto.id}
AND tt.INSTRUCTION_DOC_NUM LIKE CONCAT('%',#{dto.instructionDocNum},'%')
AND tt.INSTRUCTION_DOC_STATUS = #{dto.instructionDocStatus}
