mapper.select:
select t1.TENANT_ID, t1.LOCATOR_ID, t1.LOCATOR_CODE, t2.LOCATOR_NAME, t2.LOCATOR_LOCATION, t1.LOCATOR_TYPE, t1.LOCATOR_GROUP_ID, t1.LENGTH, t1.WIDTH, t1.HEIGHT, t1.SIZE_UOM_ID, t1.MAX_WEIGHT, t1.WEIGHT_UOM_ID, t1.MAX_CAPACITY, t1.ENABLE_FLAG, t1.PARENT_LOCATOR_ID, t1.LOCATOR_CATEGORY, t1.NEGATIVE_FLAG, t1.CID, t1.OBJECT_VERSION_NUMBER, t1.CREATED_BY, t1.CREATION_DATE, t1.LAST_UPDATED_BY, t1.LAST_UPDATE_DATE from mt_mod_locator t1 INNER join mt_mod_locator_tl t2 on (t1.LOCATOR_ID=t2.LOCATOR_ID and t2.LANG=#{lang}) where t1.TENANT_ID = ${tenantId}
and ${whereSql}
and 1=-1


SELECT
b.LOCATOR_ID,
b.LOCATOR_CODE,
l.LOCATOR_NAME,
l.LOCATOR_LOCATION,
b.LOCATOR_TYPE,
b.LOCATOR_CATEGORY,
b.LOCATOR_GROUP_ID,
ware.LOCATOR_GROUP_CODE,
b.ENABLE_FLAG,
b.NEGATIVE_FLAG
FROM
mt_mod_locator b
INNER JOIN mt_mod_locator_tl L ON b.LOCATOR_ID = l.LOCATOR_ID
AND l.LANG = #{lang}
LEFT JOIN mt_uom b1 ON b1.UOM_ID = b.SIZE_UOM_ID and b1.TENANT_ID = #{tenantId}
LEFT JOIN mt_uom b2 ON b2.UOM_ID = b.WEIGHT_UOM_ID and b2.TENANT_ID = #{tenantId}
LEFT JOIN mt_uom_tl b1_tl ON b1.UOM_ID = b1_tl.UOM_ID
AND b1_tl.LANG = #{lang}
LEFT JOIN mt_uom_tl b2_tl ON b2.UOM_ID = b2_tl.UOM_ID
AND b2_tl.LANG = #{lang}
LEFT JOIN mt_mod_locator_group ware ON ware.LOCATOR_GROUP_ID = b.LOCATOR_GROUP_ID and ware.TENANT_ID =
${tenantId}
LEFT JOIN mt_mod_locator_group_tl ware_tl ON ware_tl.LOCATOR_GROUP_ID = ware.LOCATOR_GROUP_ID
AND ware_tl.LANG = #{lang}
WHERE
b.TENANT_ID = ${tenantId}

    and b.LOCATOR_CODE like CONCAT('%',#{dto.locatorCode},'%')



    and l.LOCATOR_NAME like CONCAT('%',#{tmpLocatorName},'%')


    and b.LOCATOR_TYPE = #{dto.locatorType}


    and b.LOCATOR_CATEGORY = #{dto.locatorCategory}


    and l.LOCATOR_LOCATION like CONCAT('%',CONCAT(#{dto.locatorLocation},'%'))


    and b.LOCATOR_GROUP_ID = #{dto.locatorGroupId}


    and b.ENABLE_FLAG = #{dto.enableFlag}

ORDER BY b.CREATION_DATE DESC


select
t1.LOCATOR_ID,
t1.LOCATOR_CODE,
t2.LOCATOR_NAME,
t2.LOCATOR_LOCATION,
t1.LOCATOR_TYPE,
t1.LOCATOR_GROUP_ID,
t1.LENGTH,
t1.WIDTH,
t1.HEIGHT,
t1.SIZE_UOM_ID,
t1.MAX_WEIGHT,
t1.WEIGHT_UOM_ID,
t1.MAX_CAPACITY,
t1.ENABLE_FLAG,
t1.PARENT_LOCATOR_ID,
t1.LOCATOR_CATEGORY,
t1.NEGATIVE_FLAG
from mt_mod_locator t1 INNER join mt_mod_locator_tl t2 on (t1.LOCATOR_ID=t2.LOCATOR_ID and t2.LANG=#{locale})
where
t1.TENANT_ID = #{tenantId}

    and t1.LOCATOR_ID = #{dto.locatorId}


    and t1.LOCATOR_CODE = #{dto.locatorCode}


    and t2.LOCATOR_NAME like concat('%',#{dto.locatorName} ,'%')


    and t2.LOCATOR_LOCATION like concat('%',#{dto.locatorLocation} ,'%')


    and t1.ENABLE_FLAG = #{dto.enableFlag}


    and t1.LOCATOR_TYPE = #{dto.locatorType}


    and t1.LOCATOR_CATEGORY = #{dto.locatorCategory}


    and t1.NEGATIVE_FLAG = #{dto.negativeFlag}


    and t1.LOCATOR_GROUP_ID = #{dto.locatorGroupId}


    and t1.PARENT_LOCATOR_ID = #{dto.parentLocatorId}

order by CAST(t1.LOCATOR_ID AS DECIMAL (36))

select PARENT_LOCATOR_ID
from mt_mod_locator
where TENANT_ID = ${tenantId}
and ${parentLocatorIds}

select
*
from mt_mod_locator
where TENANT_ID = ${tenantId}
AND ${locatorCodes}


select
t1.LOCATOR_ID,
t1.PARENT_LOCATOR_ID
from mt_mod_locator t1 INNER join mt_mod_locator_tl t2 on (t1.LOCATOR_ID=t2.LOCATOR_ID and t2.LANG=#{locale})
where
t1.TENANT_ID = #{tenantId}

    and t1.LOCATOR_ID = #{dto.locatorId}


    and t2.LOCATOR_NAME = #{dto.locatorName}


    and t2.LOCATOR_LOCATION = #{dto.locatorLocation}


    and t1.ENABLE_FLAG = #{dto.enableFlag}


    and t1.LOCATOR_TYPE = #{dto.locatorType}


    and t1.LOCATOR_CATEGORY = #{dto.locatorCategory}


    and t1.NEGATIVE_FLAG = #{dto.negativeFlag}


    and t1.LOCATOR_GROUP_ID = #{dto.locatorGroupId}


    and t1.PARENT_LOCATOR_ID = #{dto.parentLocatorId}
