mapper.select:
select mid.INSTRUCTION_DOC_TYPE, mid.INSTRUCTION_DOC_NUM, mid.INSTRUCTION_DOC_STATUS, mc.COSTCENTER_CODE, mc.DESCRIPTION, mid.REMARK, mia_num.ATTR_VALUE line_num, mi.INSTRUCTION_TYPE, mi.INSTRUCTION_STATUS, mm.MATERIAL_CODE, mm.MATERIAL_NAME, mia_m.ATTR_VALUE material_version, mu.UOM_CODE, mmb.ITEM_GROUP, wig.item_group_description, mi.QUANTITY, ifnull( ( SELECT sum( mia.actual_qty ) FROM mt_instruction_actual mia WHERE mia.instruction_id = mi.instruction_id ), 0 ) actual_quantity, from_wh.locator_code from_warehouse_code, from_loc.locator_code from_locator_code, to_wh.locator_code to_warehouse_code, to_loc.locator_code to_locator_code, mia_es.attr_value excess_setting, mia_ev.attr_value excess_value, mia_so.ATTR_VALUE so_num, mia_so_line.ATTR_VALUE so_line, mia_po.ATTR_VALUE po_type, usr.real_name, mid.CREATION_DATE, ms.SUPPLIER_CODE, ms.SUPPLIER_NAME, usr2.real_name excute_Real_Name, mi.LAST_UPDATE_DATE from mt_instruction_doc mid left join mt_costcenter mc on mc.COSTCENTER_ID = mid.COST_CENTER_ID left join mt_supplier ms on ms.supplier_id = mid.SUPPLIER_ID, mt_instruction mi left join mt_instruction_attr mia_num on mia_num.instruction_id = mi.instruction_id and mia_num.attr_name = 'INSTRUCTION_LINE_NUM' left join mt_instruction_attr mia_po on mia_po.instruction_id = mi.instruction_id and mia_po.attr_name = 'PO_TYPE' left join mt_instruction_attr mia_so on mia_so.instruction_id = mi.instruction_id and mia_so.attr_name = 'SO_NUM' left join mt_instruction_attr mia_so_line on mia_so_line.instruction_id = mi.instruction_id and mia_so_line.attr_name = 'SO_LINE_NUM' left join mt_instruction_attr mia_m on mia_m.instruction_id = mi.instruction_id and mia_m.attr_name = 'MATERIAL_VERSION' LEFT JOIN mt_mod_locator from_wh ON from_wh.locator_id = mi.from_locator_id LEFT JOIN mt_instruction_attr mia_fl ON mia_fl.instruction_id = mi.instruction_id AND mia_fl.attr_name = 'FROM_LOCATOR_ID' LEFT JOIN mt_mod_locator from_loc ON from_loc.locator_id = mia_fl.attr_value LEFT JOIN mt_mod_locator to_wh ON to_wh.locator_id = mi.to_locator_id LEFT JOIN mt_instruction_attr mia_tl ON mia_tl.instruction_id = mi.instruction_id AND mia_tl.attr_name = 'TO_LOCATOR_ID' LEFT JOIN mt_mod_locator to_loc ON to_loc.locator_id = mia_tl.attr_value LEFT JOIN mt_instruction_attr mia_es ON mia_es.instruction_id = mi.instruction_id AND mia_es.attr_name = 'EXCESS_SETTING' left join mt_instruction_attr mia_ev on mia_ev.instruction_id = mi.instruction_id and mia_ev.attr_name = 'EXCESS_VALUE', mt_material mm, mt_material_site mms left join mt_material_basic mmb on mmb.material_site_id = mms.material_site_id left join wms_item_group wig on wig.item_group_code = mmb.item_group, mt_uom mu, hzero_platform.iam_user usr, hzero_platform.iam_user usr2 where mid.instruction_doc_id = mi.source_doc_id and mm.material_id = mi.material_id and mu.uom_id = mm.primary_uom_id and usr.id = mid.created_by and usr2.id = mi.last_updated_by and mms.material_id = mm.material_id and mms.site_id = mid.site_id
and mid.INSTRUCTION_DOC_TYPE = #{dto.instructionDocType}
and mc.COSTCENTER_ID = #{dto.costCenterId}
and mid.created_by = #{dto.userId}
and mi.INSTRUCTION_STATUS = #{dto.instructionDocStatus}
and wig.item_group_id = #{dto.itemGroupId}
and mid.INSTRUCTION_DOC_NUM in
#{instructionDocNum}
and from_wh.locator_code in
#{fromWarehouseCode}
and from_loc.locator_code in
#{fromLocatorCode}
and to_wh.locator_code in
#{toWarehouseCode}
and to_loc.locator_code in
#{toLocatorCode}
and mm.MATERIAL_CODE in
#{materialCode}
and mid.CREATION_DATE >= #{dto.creationDateFrom}
and mid.CREATION_DATE <= #{dto.creationDateFromTo}
and mi.LAST_UPDATE_DATE >= #{dto.lastUpdateDateFrom}
and mi.LAST_UPDATE_DATE <= #{dto.lastUpdateDateTo}
